import { useParams, Link } from 'react-router-dom';
import React, { useState, useEffect, useCallback } from 'react';
import './style.css';

const Product = () => {
  const { id } = useParams();

  const [product, setProduct] = useState({}); // Update to use an object as initial state

  const getProducts = useCallback(async () => {
    try {
      const response = await fetch(`https://dummyjson.com/products/${id}`);
      const data = await response.json();
      setProduct(data);
    } catch (error) {
      console.log(error.message);
    }
  }, [id]);

  useEffect(() => {
    getProducts();
  }, [getProducts]);

  return (
    <>
      <div>
        <h1 id='name'> {product.title} </h1>
        {/* Check if the image URL exists before rendering */}
        {product.image && <img className="image" alt="" src={product.image} />}
        <p>DESCRIPTION: {product.description}</p>
        <p>{product.price} ksh</p>
      </div>

      <Link to="/Products">
        <button>BACK</button>
      </Link>
    </>
  );
};

export default Product;
